use hex;
use zksync_web3_rs::{zks_utils::ECPAIRING_PRECOMPILE_ADDRESS, types::Bytes};

mod test_utils;
use test_utils::{eth_call, era_call};

// Puts the given data into the ECPAIRING precompile
#[tokio::test]
async fn ecpairing_empty_data_insufficient_gas() {
	let eth_response = eth_call(ECPAIRING_PRECOMPILE_ADDRESS, None, Some(Bytes::from(hex::decode("").unwrap()))).await.unwrap();
	let era_response = era_call(ECPAIRING_PRECOMPILE_ADDRESS, None, Some(Bytes::from(hex::decode("").unwrap()))).await.unwrap();
	assert_eq!(eth_response, era_response, "Puts the given data into the ECPAIRING precompile");
}

