use zksync_web3_rs::{
    types::{Address, Bytes, H160},
    zks_utils::MODEXP_PRECOMPILE_ADDRESS,
};

#[cfg(test)]
mod test_utils;
use test_utils::{era_call, eth_call};

use crate::test_utils::{parse_call_result, write_modexp_gas_result};

pub const CHEAPER_MODEXP_PRECOMPILE_ADDRESS: Address = H160([
    0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
    0x00, 0x00, 0x00, 0x25,
]);

#[tokio::test]
async fn modexp_one_limb_0_0_0() {
    let eth_response = eth_call(MODEXP_PRECOMPILE_ADDRESS, None, Some(Bytes::from(hex::decode("").unwrap()))).await.unwrap();
    let era_response = era_call(CHEAPER_MODEXP_PRECOMPILE_ADDRESS, None, Some(Bytes::from(hex::decode("").unwrap()))).await.unwrap();
    let (era_output, gas_used) = parse_call_result(&era_response);
    write_modexp_gas_result(gas_used);
    assert_eq!(eth_response, era_output);
}

